import React from "react";

class List extends React.Component{
    constructor(props){
        super(props)
        this.removeItem = this.removeItem.bind(this);
    }

    removeItem(id){
        removeChore(id)
    }
    render(){
        return(
            <div>
                <h1>Chores</h1>
                    {this.props.items.map((item, index) =>
                        <li key={index}>{item}<button type="button" onClick={() => this.removeItem(item.id)}>Delete</button></li>)}
            </div>
        )
    }
}

export default List
