import './App.css';
import React from 'react';
import New from '../components/newTodo';
import List from '../components/list';
import Filter from '../components/filter';

class App extends React.Component{
    constructor(props){
      super(props)
      this.state = {chores: ["Laundry", ""]};
      this.addChore = this.addChore.bind(this);
    }

    addChore(value){
      console.log("hello "+value);
      this.setState(prevState => ({chores: [prevState.chores, value]}))
      console.log("hello chore");
    }
  render(){
    return(
      <div>
        <h1>Your to do list</h1>
        < New addChore={this.addChore}/>
        < List items={this.state.chores}/>
        < Filter />
      </div>
    )
  }
}

export default App;
